import React, { useState } from "react";
import WeatherCard from "./WeatherCard";
import DaysWeatherCard from "./DaysWeatherCard";
import axios from "axios";
import callAPI from "../scripts/callAPI"

export default function Header() {

    let [data,setData]=useState({})

    const [page, setPage] = useState()
    let pages = []

    const handleClick = (event) => {

        const id = event.target.dataset.id
        switch (id) {
            case "1":
                pages = []
                pages.push(<WeatherCard data={data} />)
                break;

            case "2":
                if(data.name){
                    setPage(<DaysWeatherCard data={data}/>)
                    
                }
                else{
                    setPage(<DaysWeatherCard/>)
                }
                break;

            default:
                setPage(<WeatherCard />)
                break;
        }
    }

    const handleEnter = (event) =>{
        if(event.key === "Enter"){
            callAPI.searchLocation().then((response) => {
                setData(response)
            })
        }        
    }


    return (

        <>
            <div className="input--Field">
                <div>
                    <button className="btn-days" onClick={handleClick} data-id="1">One day</button>
                </div>
                <div className="input">

                    <input
                        value={callAPI.location}
                        onChange={event => callAPI.setLocation(event.target.value)}
                        onKeyDown={handleEnter}
                        type="text"
                        placeholder="Enter Location" />

                </div>
                <div>
                    <button className="btn-days" onClick={handleClick} data-id="2">Days</button>
                </div>
            </div>
            {/* <WeatherCard data={data} /> */}
            {pages.map((pg) => {
                console.log("wa")
                return pg
            })}
        </>

    )

}
