import React, { useState } from "react";
import axios from "axios";


export default function WeatherCard(){

    const [data,setData] = useState({})
    const [location,setLocation] = useState('')

    const url = `https://api.openweathermap.org/data/2.5/weather?q=${location}&appid=187660880dcb3b894d9fbe3ab7efa20d`

    const searchLocation = (event) => {

        if(event.key === 'Enter'){

            axios.get(url).then((response) =>{
                setData(response.data)
                console.log(response.data)
            })
        }
    }

    return(

        <div className="card">
            <div className="card--city">
                <p className="card--city-name">Ploiesti</p>
                <p className="card--city-description">Sunny</p>
            </div>
            <div className="card--info">
                <p><i class="fa-regular fa-sun"></i><span>30℃</span></p> 
                <p><i class="fa-solid fa-wind"></i><span>ceva</span></p>
                <p><i class="fa-solid fa-droplet"></i><span>ceva%</span></p> 
                <p><i class="fa-solid  fa-cloud-rain"></i><span>L/m²</span></p>
            </div>
        </div>
    )
}
